import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function Search() {
  const [query, setQuery] = useState('');

  const handleInputChange = (event) => {
    setQuery(event.target.value);
  };

  const handleSearch = () => {
    const apiKey = '553816bd9e804a64a8481341230108';
    const apiUrl = `https://cors-anywhere.herokuapp.com/https://api.weatherapi.com/v1/current.json?key=${apiKey}&q=${query}&aqi=no`;
  
    axios.get(apiUrl)
      .then(response => {
        console.log('Weather data:', response.data);
        // For example, you can set the weather data to a state variable to display it in the UI.
        // setWeatherData(response.data);
      })
      .catch(error => {
        console.error('Error fetching weather data:', error);
      });
  };
  

  return (
    <div className="search-container">
     <br />
      <input
        className="Search-input"
        type="text"
        placeholder="Search for a place..."
        value={query}
        onChange={handleInputChange}
      />
      <br />
      <br />
      
      <button className="button" onClick={handleSearch}>Search</button>
    </div>
  );
}

export default Search;
